r {\"height\":\"0px\"} -->\n<div style=\"height:0px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:spacer {\"height\":\"20px\"} -->\n<div style=\"height:20px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:columns {\"lock\":{\"move\":false,\"remove\":false},\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":239,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-full\"><img src=\"http://localhost/wordpress/wp-content/uploads/2023/06/programas4.jpg\" alt=\"\" class=\"wp-image-239\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"25px\"},\"color\":{\"text\":\"#cab86a\"}}} -->\n<h2 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#cab86a;font-size:25px\">Atelier Avanzar</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>A place to learn, work, earn a living, and meet like-minded people.&nbsp;<a href=\"https://www.fundacion-avanzar.com/programs-programas/atelier-avanzar/\">See more...</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Un lugar donde aprender, trabajar, ganarse la vida y conocer gente con ideas afines.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><a href=\"https://www.fundacion-avanzar.com/programs-programas/atelier-avanzar/\">Ver más...</a></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"left\",\"id\":97,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image alignleft size-full\"><img src=\"http://localhost/wordpress/wp-content/uplo