(237, 1, '2023-06-29 09:48:35', '2023-06-29 14:48:35', '<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"wp-block-heading has-text-align-center\">PROGRAMS / PROGRAMAS</h2>\n<!-- /wp:heading -->\n\n<!-- wp:separator {\"align\":\"wide\",\"style\":{\"color\":{\"background\":\"#b0884b\"}}} -->\n<hr class=\"wp-block-separator alignwide has-text-color has-alpha-channel-opacity has-background\" style=\"background-color:#b0884b;color:#b0884b\"/>\n<!-- /wp:separator -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">AVANZAR has developed a number of humanitarian and educational programs aimed at&nbsp;underprivileged women who wish to improve their lives&nbsp;and those of their families, as well as for children with illnesses and their relatives.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">                                                                                           ________________________________________________</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">AVANZAR ha desarrollado una serie de programas humanitarios y educativos dirigidos a mujeres desfavorecidas que desean mejorar su vida y la de sus familias, así como a niños con enfermedades y a sus familiares.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:separator {\"align\":\"wide\",\"style\":{\"color\":{\"background\":\"#b0884b\"}}} -->\n<hr class=\"wp-block-separator alignwide has-text-color has-alpha-channel-opacity has-background\" style=\"background-color:#b0884b;color:#b0884b\"/>\n<!-- /wp:separator -->\n\n<!-- wp:cover {\"overlayColor\":\"nv-site-bg\",\"minHeight\":915,\"minHeightUnit\":\"px\",\"align\":\"wide\"} -->\n<div class=\"wp-block-cover alignwide\" style=\"min-height:915px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-nv-site-bg-background-color has-background-dim-100 has-background-dim\"></span><div class=\"wp-block-cover__inner-container\"><!-- wp:columns {\"lock\":{\"move\":false,\"remove\":false}} -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"left\",\"id\":141,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image alignleft size-full\"><img src=\"http://localhost/wordpress/wp-content/uploads/2023/06/noticia3jpg.jpg\" alt=\"\" class=\"wp-image-141\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":3,\"style\":{\"color\":{\"text\":\"#bdbd14\"}}} -->\n<h3 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#bdbd14\">Training Programs / Programas de Entrenamiento</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"textColor\":\"neve-text-color\"} -->\n<p class=\"has-neve-text-color-color has-text-color\">Courses to professionalize all aspects of work skills.&nbsp;<a href=\"https://www.fundacion-avanzar.com/programs-programas/training-programs-programas-de-entrenamiento/\">See more...</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"neve-text-color\"} -->\n<p class=\"has-neve-text-color-color has-text-color\">Cursos para profesionalizar los aspectos de las competencias laborales.&nbsp;<a href=\"https://www.fundacion-avanzar.com/programs-programas/training-programs-programas-de-entrenamiento/\">Ver más...</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Ich habe so viel neues Wissen aus diesen vier Wochen mitnehmen können, welches mir mein Leben lang bleiben wird, und ich bin unglaublich dankbar dafür, dass ich dank Margarita und der Fundacion Avanzar dieses einzigartige Erlebnis machen durfte.&nbsp;</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"left\",\"id\":186,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image alignleft size-full\"><img src=\"http://localhost/wordpress/wp-content/uploads/2023/06/COLUMNAjpg.jpg\" alt=\"\" class=\"wp-image-186\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":3,\"style\":{\"color\":{\"text\":\"#bdbd14\"}}} -->\n<h3 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#bdbd14\"><strong><a href=\"https://www.fundacion-avanzar.com/volunteers-voluntarios/lotte/\">Financial Education / Educación Financiera</a></strong></h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"textColor\":\"nv-dark-bg\"} -->\n<p class=\"has-nv-dark-bg-color has-text-color\">Courses to professionalize work knowledge and improve personal aspects.&nbsp;<a href=\"https://www.fundacion-avanzar.com/programs-programas/financial-education-educaci%C3%B3n-financiera/\">See more..</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"nv-dark-bg\"} -->\n<p class=\"has-nv-dark-bg-color has-text-color\">Cursos para profesionalizar conocimientos laborales y mejorar aspectos personales.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"nv-dark-bg\"} -->\n<p class=\"has-nv-dark-bg-color has-text-color\"><a href=\"https://www.fundacion-avanzar.com/programs-programas/financial-education-educaci%C3%B3n-financiera/\">Ver más..</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"left\",\"id\":203,\"sizeSlug\":\"full\"